pragma solidity ^0.4.0;

contract InterChainV2 {

	event newTransactionSubmitted(uint256 txHash, address _to, uint256 _amount);
	event addressRecovered(address recovered);
	event hashedMessage(string message, bytes32 hashed, bytes32 msgHash);

	address[] public authorities;
	uint256 public required;
	mapping(uint256 => Transaction) transactions;

	struct Transaction {
		address to;
		uint256 amount;
		bool executed;
		bool exists;
	}

	function InterChainV2(address[] _authorities, uint256 _required) public{
		authorities = _authorities;
		required = _required;
	}


	// here we will assume that the msgHash is sha3 of msg in the following format
	// concat toAddress + value
	function submitTransaction(bytes32 msgHash, string message, uint8[] v, bytes32[] r, bytes32[] s) public {
		bytes32 hashed = keccak256(message);
		emit hashedMessage(message, hashed, msgHash);

		for (uint8 i = 0; i < v.length; i++) {
			address res = ecrecover(msgHash, v[i], r[i], s[i]);
			emit addressRecovered(res);
		}
	}

	function recoverAddr(bytes32 msgHash, uint8 v, bytes32 r, bytes32 s) public returns (address) {
		return ecrecover(msgHash, v, r, s);
	}
}
